import React, { useEffect, useState } from 'react';
import {
  Box,
  Paper,
  Typography,
  Grid,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Chip,
  Divider,
  Collapse,
  Alert,
  CircularProgress,
  Button
} from '@mui/material';
import {
  Warning as WarningIcon,
  CheckCircle as CheckCircleIcon,
  Error as ErrorIcon,
  ExpandMore as ExpandMoreIcon,
  Download as DownloadIcon
} from '@mui/icons-material';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';

const vulnerabilityRecommendations = {
  xss: [
    "Implement context-aware output encoding",
    "Use Content Security Policy (CSP) headers",
    "Validate and sanitize all user inputs",
    "Set HttpOnly and Secure flags on cookies"
  ],
  sql_injection: [
    "Use prepared statements with parameterized queries",
    "Implement strict input validation",
    "Use ORM frameworks for database access",
    "Regularly update database software"
  ],
  missing_headers: [
    "Add Content-Security-Policy header",
    "Implement Strict-Transport-Security",
    "Set X-Content-Type-Options to 'nosniff'",
    "Configure X-Frame-Options to 'DENY'"
  ]
};

const ResultsView = ({ scanId, scanData }) => {
  const [status, setStatus] = useState('pending');
  const [expandedVuln, setExpandedVuln] = useState(null);

  const generateReport = () => {
    const doc = new jsPDF();
    
    // Report Header
    doc.setFontSize(18);
    doc.text(`Security Scan Report - ${new Date().toLocaleDateString()}`, 15, 20);
    
    // Scan Overview
    doc.setFontSize(12);
    doc.text(`Scanned URL: ${scanData.base_url}`, 15, 30);
    doc.text(`Pages Scanned: ${scanData.pages_scanned}`, 15, 35);
    doc.text(`Forms Found: ${scanData.forms_found}`, 15, 40);

    // Vulnerabilities Table
    doc.setFontSize(14);
    doc.text('Identified Vulnerabilities', 15, 50);
    
    const vulnData = scanData.vulnerabilities.map(vuln => [
      vuln.type,
      vuln.severity,
      vuln.url,
      vulnerabilityRecommendations[vuln.type.toLowerCase()]?.join('\n') || 'N/A'
    ]);

    doc.autoTable({
      startY: 55,
      head: [['Type', 'Severity', 'Location', 'Recommendations']],
      body: vulnData,
      theme: 'grid',
      styles: { fontSize: 10 },
      headStyles: { fillColor: [41, 128, 185] },
      columnStyles: {
        3: { cellWidth: 60 }
      }
    });

    // Subdomains Table
    doc.setFontSize(14);
    doc.text('Discovered Subdomains', 15, doc.autoTable.previous.finalY + 10);
    
    const subdomainData = scanData.subdomains.map(sub => [
      sub.subdomain,
      sub.status,
      sub.ip_address || 'N/A',
      sub.server || 'Unknown'
    ]);

    doc.autoTable({
      startY: doc.autoTable.previous.finalY + 15,
      head: [['Subdomain', 'Status', 'IP Address', 'Server']],
      body: subdomainData,
      theme: 'grid',
      styles: { fontSize: 10 },
      headStyles: { fillColor: [39, 174, 96] }
    });

    doc.save(`security_report_${scanId}.pdf`);
  };

  return (
    <Paper elevation={3} sx={{ p: 4, borderRadius: 4, mt: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h5" sx={{ fontWeight: 600 }}>
          Scan Results
        </Typography>
        <Button
          variant="contained"
          startIcon={<DownloadIcon />}
          onClick={generateReport}
          sx={{ borderRadius: 2 }}
        >
          Download Report
        </Button>
      </Box>

      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2, borderRadius: 2 }}>
            <Typography variant="subtitle2" color="textSecondary">
              Total Vulnerabilities
            </Typography>
            <Typography variant="h3" color="error.main">
              {scanData.vulnerabilities.length}
            </Typography>
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2, borderRadius: 2 }}>
            <Typography variant="subtitle2" color="textSecondary">
              High Risk Issues
            </Typography>
            <Typography variant="h3" color="warning.main">
              {scanData.vulnerabilities.filter(v => v.severity === 'High').length}
            </Typography>
          </Paper>
        </Grid>
      </Grid>

      <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
        Vulnerability Details
      </Typography>
      
      <List>
        {scanData.vulnerabilities.map((vuln, index) => (
          <React.Fragment key={index}>
            <ListItem 
              button 
              onClick={() => setExpandedVuln(index === expandedVuln ? null : index)}
              sx={{ borderRadius: 2 }}
            >
              <ListItemIcon>
                <WarningIcon color={
                  vuln.severity === 'High' ? 'error' : 
                  vuln.severity === 'Medium' ? 'warning' : 'info'
                } />
              </ListItemIcon>
              <ListItemText
                primary={vuln.type}
                secondary={`Found in: ${vuln.url}`}
              />
              <Chip
                label={vuln.severity}
                color={
                  vuln.severity === 'High' ? 'error' : 
                  vuln.severity === 'Medium' ? 'warning' : 'info'
                }
                sx={{ mr: 2 }}
              />
              <ExpandMoreIcon
                sx={{
                  transform: index === expandedVuln ? 'rotate(180deg)' : 'none',
                  transition: 'transform 0.2s'
                }}
              />
            </ListItem>
            
            <Collapse in={index === expandedVuln}>
              <Box sx={{ pl: 6, pr: 2, py: 2, bgcolor: 'background.default' }}>
                <Divider sx={{ mb: 2 }} />
                <Typography variant="body2" paragraph>
                  <strong>Description:</strong> {vuln.description}
                </Typography>
                <Typography variant="body2" paragraph>
                  <strong>Recommendations:</strong>
                </Typography>
                <List dense sx={{ listStyleType: 'disc', pl: 4 }}>
                  {(vulnerabilityRecommendations[vuln.type.toLowerCase()] || []).map((rec, i) => (
                    <ListItem key={i} sx={{ display: 'list-item' }}>
                      <Typography variant="body2">{rec}</Typography>
                    </ListItem>
                  ))}
                </List>
                {vuln.proof_of_concept && (
                  <Typography variant="caption" color="textSecondary">
                    <strong>Proof of Concept:</strong> {vuln.proof_of_concept}
                  </Typography>
                )}
              </Box>
            </Collapse>
            <Divider />
          </React.Fragment>
        ))}
      </List>
    </Paper>
  );
};

export default ResultsView;
