import React, { useEffect, useState } from 'react';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';
import {
  Box,
  Paper,
  Typography,
  Grid,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Chip,
  Divider,
  Collapse,
  Alert,
  CircularProgress,
  Button
} from '@mui/material';
import {
  Warning as WarningIcon,
  CheckCircle as CheckCircleIcon,
  Error as ErrorIcon,
  ExpandMore as ExpandMoreIcon,
  Domain as DomainIcon,
  Download as DownloadIcon
} from '@mui/icons-material';

// Constants for vulnerability recommendations
const vulnerabilityRecommendations = {
  xss: [
    "Implement context-aware output encoding",
    "Use Content Security Policy (CSP) headers",
    "Validate and sanitize all user inputs",
    "Set HttpOnly and Secure flags on cookies"
  ],
  sql_injection: [
    "Use prepared statements with parameterized queries",
    "Implement strict input validation",
    "Use ORM frameworks for database access",
    "Regularly update database software"
  ],
  missing_security_headers: [
    "Add Content-Security-Policy header",
    "Implement Strict-Transport-Security",
    "Set X-Content-Type-Options to 'nosniff'",
    "Configure X-Frame-Options to 'DENY'"
  ]
};

// Helper function to determine severity color
const severityColor = (severity) => {
  switch(severity.toLowerCase()) {
    case 'critical': return 'error';
    case 'high': return 'error';
    case 'medium': return 'warning';
    case 'low': return 'info';
    default: return 'default';
  }
};

const ResultsView = ({ scanId, onResults }) => {
  const [status, setStatus] = useState('pending');
  const [data, setData] = useState(null);
  const [expandedVuln, setExpandedVuln] = useState(null);

  // PDF Generation Function
  const generatePdfReport = () => {
    const doc = new jsPDF();
    const pageWidth = doc.internal.pageSize.width;
    const marginLeft = 15;
    
    // Helper function for section headers
    const addSectionHeader = (text, y) => {
      doc.setFillColor(52, 73, 94);
      doc.rect(marginLeft - 5, y - 5, pageWidth - 20, 10, 'F');
      doc.setTextColor(255, 255, 255);
      doc.setFontSize(14);
      doc.text(text, marginLeft, y);
      doc.setTextColor(0, 0, 0);
      return y + 15;
    };

    // Title and Header
    doc.setFontSize(24);
    doc.setTextColor(44, 62, 80);
    doc.text('Security Scan Report', marginLeft, 20);
    
    doc.setFontSize(12);
    doc.setTextColor(127, 140, 141);
    doc.text(`Generated on ${new Date().toLocaleDateString()}`, marginLeft, 30);
    
    // Scan Overview
    let yPos = addSectionHeader('Scan Overview', 45);
    
    doc.setFontSize(11);
    doc.setTextColor(52, 73, 94);
    const overviewData = [
      ['Target URL:', data.base_url],
      ['Pages Scanned:', data.pages_scanned.toString()],
      ['Forms Found:', data.forms_found.toString()],
      ['Total Vulnerabilities:', data.vulnerabilities.length.toString()],
      ['Subdomains Discovered:', data.subdomains.length.toString()]
    ];
    
    overviewData.forEach(([label, value]) => {
      doc.setFont(undefined, 'bold');
      doc.text(label, marginLeft, yPos);
      doc.setFont(undefined, 'normal');
      doc.text(value, marginLeft + 40, yPos);
      yPos += 7;
    });

    // Vulnerabilities Section
    yPos = addSectionHeader('Security Vulnerabilities', yPos + 10);
    
    const vulnData = data.vulnerabilities.map(vuln => [
      vuln.vulnerability_type,
      vuln.severity,
      vuln.url,
      vulnerabilityRecommendations[vuln.vulnerability_type.toLowerCase()]
        ?.slice(0, 2)
        .join('\n') || vuln.recommendation
    ]);

    doc.autoTable({
      startY: yPos,
      head: [['Vulnerability', 'Severity', 'Location', 'Key Recommendations']],
      body: vulnData,
      theme: 'striped',
      styles: {
        fontSize: 10,
        cellPadding: 5
      },
      headStyles: {
        fillColor: [41, 128, 185],
        textColor: 255,
        fontStyle: 'bold'
      },
      columnStyles: {
        0: { cellWidth: 40 },
        1: { cellWidth: 30 },
        2: { cellWidth: 50 },
        3: { cellWidth: 70 }
      },
      alternateRowStyles: {
        fillColor: [241, 245, 249]
      }
    });

    // Subdomain Section
    yPos = doc.autoTable.previous.finalY + 15;
    yPos = addSectionHeader('Discovered Subdomains', yPos);

    const subdomainData = data.subdomains.map(sub => [
      sub.subdomain,
      sub.status,
      sub.ip_address || 'N/A',
      sub.server || 'Unknown'
    ]);

    doc.autoTable({
      startY: yPos,
      head: [['Subdomain', 'Status', 'IP Address', 'Server']],
      body: subdomainData,
      theme: 'striped',
      styles: {
        fontSize: 10,
        cellPadding: 5
      },
      headStyles: {
        fillColor: [39, 174, 96],
        textColor: 255,
        fontStyle: 'bold'
      },
      alternateRowStyles: {
        fillColor: [241, 245, 249]
      }
    });

    // Footer
    const pageCount = doc.internal.getNumberOfPages();
    doc.setFontSize(10);
    doc.setTextColor(127, 140, 141);
    for (let i = 1; i <= pageCount; i++) {
      doc.setPage(i);
      doc.text(
        `Page ${i} of ${pageCount}`,
        doc.internal.pageSize.width / 2,
        doc.internal.pageSize.height - 10,
        { align: 'center' }
      );
    }

    doc.save(`security_report_${scanId}.pdf`);
  };

  // Fetch scan results
  useEffect(() => {
    const checkStatus = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/scan/${scanId}`);
        const result = await response.json();
        
        setStatus(result.status);
        if (result.status === 'completed') {
          setData(result.results);
          onResults(result.results);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    const interval = setInterval(checkStatus, 2000);
    return () => clearInterval(interval);
  }, [scanId, onResults]);

  // Loading state
  if (status === 'pending') {
    return (
      <Paper elevation={3} sx={{ p: 4, borderRadius: 4, textAlign: 'center' }}>
        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 2 }}>
          <CircularProgress size={60} thickness={4} />
          <Typography variant="h6" color="textSecondary">
            Scanning in progress...
          </Typography>
          <Typography variant="body2" color="textSecondary">
            This may take a few minutes
          </Typography>
        </Box>
      </Paper>
    );
  }

  // Error state
  if (status === 'failed') {
    return (
      <Paper elevation={3} sx={{ p: 4, borderRadius: 4 }}>
        <Alert severity="error" icon={<ErrorIcon fontSize="large" />}>
          <Typography variant="h6">Scan Failed</Typography>
          <Typography variant="body2">Please try again or check your network connection</Typography>
        </Alert>
      </Paper>
    );
  }

  if (!data) return null;

  return (
    <Paper elevation={3} sx={{ p: 4, borderRadius: 4 }}>
      {/* Header Section */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h5" sx={{ fontWeight: 600 }}>
          Scan Results Overview
        </Typography>
        <Button
          variant="contained"
          startIcon={<DownloadIcon />}
          onClick={generatePdfReport}
          sx={{ borderRadius: 2 }}
        >
          Download Full Report
        </Button>
      </Box>

      {/* Metrics Grid */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6}>
          <Paper sx={{ p: 3, borderRadius: 3 }}>
            <Typography variant="subtitle2" color="textSecondary">Pages Scanned</Typography>
            <Typography variant="h3" sx={{ fontWeight: 700 }}>{data.pages_scanned}</Typography>
          </Paper>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Paper sx={{ p: 3, borderRadius: 3 }}>
            <Typography variant="subtitle2" color="textSecondary">Forms Found</Typography>
            <Typography variant="h3" sx={{ fontWeight: 700 }}>{data.forms_found}</Typography>
          </Paper>
        </Grid>
      </Grid>

      {/* Vulnerabilities Section */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
          Security Findings ({data.vulnerabilities.length})
        </Typography>

        {data.vulnerabilities.length === 0 ? (
          <Alert severity="success" icon={<CheckCircleIcon />}>
            No vulnerabilities detected - Great job!
          </Alert>
        ) : (
          <List sx={{ bgcolor: 'background.paper' }}>
            {data.vulnerabilities.map((vuln, index) => (
              <React.Fragment key={index}>
                <ListItem 
                  button 
                  onClick={() => setExpandedVuln(expandedVuln === index ? null : index)}
                  sx={{
                    borderRadius: 2,
                    '&:hover': { bgcolor: 'action.hover' }
                  }}
                >
                  <ListItemIcon>
                    <WarningIcon color={severityColor(vuln.severity)} />
                  </ListItemIcon>
                  <ListItemText
                    primary={vuln.vulnerability_type}
                    secondary={`Found in: ${vuln.url}`}
                  />
                  <Chip 
                    label={vuln.severity} 
                    color={severityColor(vuln.severity)}
                    sx={{ mr: 2 }}
                  />
                  <ExpandMoreIcon
                    sx={{
                      transform: expandedVuln === index ? 'rotate(180deg)' : 'none',
                      transition: 'transform 0.2s'
                    }}
                  />
                </ListItem>
                <Collapse in={expandedVuln === index}>
                  <Box sx={{ pl: 6, pr: 2, py: 2 }}>
                    <Divider sx={{ mb: 2 }} />
                    <Typography variant="body2" paragraph>
                      <strong>Description:</strong> {vuln.description}
                    </Typography>
                    <Typography variant="body2" paragraph>
                      <strong>Recommendations:</strong>
                    </Typography>
                    <List dense sx={{ listStyleType: 'disc', pl: 4 }}>
                      {[...vuln.recommendation.split('. '), ...(vulnerabilityRecommendations[vuln.vulnerability_type.toLowerCase()] || [])]
                        .filter(r => r.trim())
                        .map((rec, i) => (
                          <ListItem key={i} sx={{ display: 'list-item' }}>
                            <Typography variant="body2">{rec}</Typography>
                          </ListItem>
                        ))}
                    </List>
                    {vuln.proof_of_concept && (
                      <Typography variant="caption" color="textSecondary">
                        <strong>Proof of Concept:</strong> {vuln.proof_of_concept}
                      </Typography>
                    )}
                  </Box>
                </Collapse>
                <Divider />
              </React.Fragment>
            ))}
          </List>
        )}
      </Box>

      {/* Subdomains Section */}
      <Box>
        <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
          Subdomain Discovery ({data.subdomains.length})
        </Typography>
        <List>
          {data.subdomains.map((subdomain, index) => (
            <ListItem 
              key={index}
              sx={{
                borderRadius: 2,
                '&:hover': { bgcolor: 'action.hover' }
              }}
            >
              <ListItemIcon>
                <DomainIcon color={subdomain.status === 'Live' ? 'success' : 'disabled'} />
              </ListItemIcon>
              <ListItemText
                primary={subdomain.subdomain}
                secondary={`IP: ${subdomain.ip_address || 'N/A'} • Server: ${subdomain.server || 'Unknown'}`}
              />
              <Chip
                label={subdomain.status}
                color={subdomain.status === 'Live' ? 'success' : 'default'}
                variant="outlined"
              />
            </ListItem>
          ))}
        </List>
      </Box>
    </Paper>
  );
};

export default ResultsView;
