import React, { useEffect, useState } from 'react';
import LoadingSpinner from './LoadingSpinner';

export default function ResultsView({ scanId, onResults }) {
  const [status, setStatus] = useState('pending');
  const [data, setData] = useState(null);

  useEffect(() => {
    const checkStatus = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/scan/${scanId}`);
        const result = await response.json();
        
        setStatus(result.status);
        if (result.status === 'completed') {
          setData(result.results);
          onResults(result.results);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    const interval = setInterval(checkStatus, 2000);
    return () => clearInterval(interval);
  }, [scanId]);

  if (status === 'pending') {
    return (
      <div className="bg-white shadow rounded-lg p-6">
        <LoadingSpinner />
        <p className="text-center text-gray-600">Scan in progress...</p>
      </div>
    );
  }

  if (status === 'failed') {
    return (
      <div className="bg-white shadow rounded-lg p-6">
        <p className="text-red-600">Scan failed. Please try again.</p>
      </div>
    );
  }

  if (!data) return null;

  return (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-xl font-semibold mb-4">Scan Results</h2>
      
      <div className="grid grid-cols-2 gap-4 mb-6">
        <div className="p-4 bg-gray-50 rounded">
          <p className="text-sm text-gray-600">Pages Scanned</p>
          <p className="text-2xl font-bold">{data.pages_scanned}</p>
        </div>
        <div className="p-4 bg-gray-50 rounded">
          <p className="text-sm text-gray-600">Forms Found</p>
          <p className="text-2xl font-bold">{data.forms_found}</p>
        </div>
      </div>

      <div className="mb-6">
        <h3 className="text-lg font-medium mb-2">Vulnerabilities</h3>
        {data.vulnerabilities.length === 0 ? (
          <p className="text-green-600">No vulnerabilities found</p>
        ) : (
          <div className="space-y-4">
            {data.vulnerabilities.map((vuln, index) => (
              <div key={index} className="border rounded p-4">
                <p className="font-medium text-red-600">{vuln.vulnerability_type}</p>
                <p className="text-sm text-gray-600">URL: {vuln.url}</p>
                <p className="text-sm text-gray-600">Severity: {vuln.severity}</p>
              </div>
            ))}
          </div>
        )}
      </div>

      <div>
        <h3 className="text-lg font-medium mb-2">Subdomains</h3>
        <div className="space-y-2">
          {data.subdomains.map((subdomain, index) => (
            <div key={index} className="text-sm">
              <span className="font-medium">{subdomain.subdomain}</span>
              <span className="text-gray-600"> - {subdomain.status}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
